function() {

return function (itemName,itemContent,storageType) {
	var result = itemContent;

    if (storageType === undefined) {
        storageType = "local";
    }

	if ( typeof itemContent == "object" || typeof itemContent == "array" ) {
		try {
			result = JSON.stringify(itemContent);
		} finally {}
	}

	result = btoa(result);

	if (storageType == "session") {
		result = sessionStorage.setItem(itemName,result);
	} else if (storageType == "local") {
		result = localStorage.setItem(itemName,result);
	} else {
		return false;
	}
	return true;
};
}

function() {

return function (itemName,storageType) {
	var result;
    if (storageType === undefined) {
        storageType = "local";
    }

	if (storageType == "session") {
		result = sessionStorage.getItem(itemName);
	} else if (storageType == "local") {
		result = localStorage.getItem(itemName);
	} else {
		return null;
	}

	result = atob(result);
	if ( ( result[0] == "{" && result[result.length - 1] == "}" ) || ( result[0] == "[" && result[result.length - 1] == "]" ) ) {
		try {
			result = JSON.parse(result);
		} finally {}
	} else if ( !isNaN(result) ) {
      result = Number(result);
    } 
	return result;
};
}

